(4, 1, 'Информационные услуги', '<table width="100%" id="sheme_info_2">\r\n	<tr>\r\n		<td><a href="?r=4&sr=20&s2r=40"><img src="img/illustrations/sheme/ico_1.jpg" width="69" height="87" alt="SMS-услуги" title="SMS-услуги"></a></td>\r\n		<td><a href="?r=4&sr=20&s2r=41"><img src="img/illustrations/sheme/ico_2.jpg" width="69" height="87" alt="Web-отслеживание" title="Web-отслеживание"></a></td>\r\n		<td><a href="?r=4&sr=20&s2r=42"><img src="img/illustrations/sheme/ico_3.jpg" width="69" height="87" alt="TnTcheck" title="TnTcheck"></a></td>\r\n		<td><a href="?r=4&sr=20&s2r=43"><img src="img/illustrations/sheme/ico_4.jpg" width="69" height="87" alt="E-mail-сервис" title="E-mail-сервис"></a></td>\r\n		<td><a href="?r=4&sr=21&s2r=61"><img src="img/illustrations/sheme/ico_5.jpg" width="69" height="87" alt="Все о вашем контейнере" title="Все о вашем контейнере"></a></td>\r\n		<td><a href="?r=4&sr=21&s2r=44"><img src="img/illustrations/sheme/ico_6.jpg" width="69" height="87" alt="Запрос по финансовым расчетам" title="Запрос по финансовым расчетам"></a></td>\r\n		<td><a href="?r=4&sr=21&s2r=45"><img src="img/illustrations/sheme/ico_7.jpg" width="69" height="87" alt="Рассылка инвойсов" title="Рассылка инвойсов"></a></td>\r\n	</tr>\r\n	<tr>\r\n		<td colspan="7" style="text-align:center;border-top:3px solid #cccccc;"><img src="img/illustrations/sheme/ico_polaris.jpg" width="68" height="79" alt="Polaris" title="Polaris"></td>\r\n	</tr>\r\n</table>', '', '', ''),
